package com.array.programs;

public class sumOfElements {

	public static void main(String[] args) {

		int sum = 0;
		int arr[] = { 10, 20, 30, 40, 50 };

		for (int i = 0; i < 5; i++) {
			sum = sum + arr[i];
			// System.out .println(sum);
		}
		System.out.println(sum);
		// TODO Auto-generated method stub

	}

}
